import React, { useRef, useState, useEffect } from "react";
import axios from "axios";

const RegisterFace = () => {
  const videoRef = useRef(null);
  const [name, setName] = useState("");
  const [cameraOn, setCameraOn] = useState(false);

  // Start webcam
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      setCameraOn(true);
    } catch (err) {
      alert("⚠️ Camera access denied or unavailable.");
      console.error(err);
    }
  };

  // Stop webcam
  const stopCamera = () => {
    const stream = videoRef.current?.srcObject;
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
    }
    setCameraOn(false);
  };

  // Capture image and send to backend
  const capture = async () => {
    if (!name.trim()) return alert("⚠️ Please enter a name");

    const canvas = document.createElement("canvas");
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;

    const ctx = canvas.getContext("2d");
    ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    const base64Image = canvas.toDataURL("image/jpeg");

    try {
      const res = await axios.post("http://localhost:5000/register", {
        name,
        image: base64Image,
      });

      if (res.data.status === "success") {
        alert(`✅ ${name} registered successfully!`);
        setName("");
      } else {
        alert("❌ Registration failed");
      }
    } catch (err) {
      console.error(err);
      alert("❌ Failed to register: " + err?.response?.data?.msg || "Unknown error");
    }
  };

  useEffect(() => {
    return () => stopCamera(); // cleanup on unmount
  }, []);

  return (
    <div className="p-4 space-y-4 max-w-xl mx-auto text-center">
      <h2 className="text-2xl font-semibold">Register Face</h2>

      {!cameraOn && (
        <button
          onClick={startCamera}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Start Cam
        </button>
      )}

      {cameraOn && (
        <>
          <video
            ref={videoRef}
            autoPlay
            className="mx-auto rounded border border-gray-300 shadow-md"
            style={{ width: "100%", maxWidth: "480px" }}
          />

          <input
            type="text"
            placeholder="Enter name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="border p-2 rounded w-full mt-2"
          />

          <div className="flex gap-4 justify-center mt-2">
            <button
              onClick={capture}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            >
              Register
            </button>
            <button
              onClick={stopCamera}
              className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
            >
              Stop Cam
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default RegisterFace;
