import React, { useRef } from "react";

const LiveCam = () => {
  const videoRef = useRef();

  const startCamera = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;
  };

  return (
    <div className="p-4">
      <h2 className="text-xl mb-2">Live Camera</h2>
      <video ref={videoRef} autoPlay className="w-full h-auto rounded border" />
      <button onClick={startCamera} className="mt-4 bg-blue-500 text-white p-2 rounded">
        Start Camera
      </button>
    </div>
  );
};

export default LiveCam;
