    <div className="space-y-3 text-center">
      <video ref={videoRef} width="640" height="480" autoPlay className="mx-auto rounded border" />
      <div>
        <input
          className="border px-2 py-1 rounded mr-2"
          placeholder="Enter name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <button className="bg-blue-600 text-white px-4 py-1 rounded mr-2" onClick={capture}>
          Register
