import React, { useEffect, useRef } from "react";
import axios from "axios";

export default function LiveCam() {
  const videoRef = useRef(null);

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true }).then(
      (stream) => (videoRef.current.srcObject = stream)
    );
    // Poll every second for demo (send single frame, get boxes back)…
    // Implementation of server-side matching not included here.
  }, []);

  return (
    <div className="text-center">
      <video ref={videoRef} width="640" height="480" autoPlay className="mx-auto rounded border" />
      <p className="mt-2 text-sm text-gray-500">Live recognition coming soon…</p>
    </div>
  );
}
