import React, { useRef } from "react";

const RegisterFace = () => {
  const videoRef = useRef();
  const canvasRef = useRef();

  const captureAndRegister = async () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(videoRef.current, 0, 0, 640, 480);

    const dataUrl = canvas.toDataURL("image/jpeg");
    const response = await fetch("http://localhost:5000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ image: dataUrl }),
    });

    const result = await response.json();
    alert(result.message);
  };

  const startCamera = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;
  };

  return (
    <div className="p-4">
      <h2 className="text-xl mb-2">Register Face</h2>
      <video ref={videoRef} autoPlay className="rounded border" width="640" height="480" />
      <canvas ref={canvasRef} width="640" height="480" hidden />
      <div className="mt-4 flex gap-2">
        <button onClick={startCamera} className="bg-green-500 text-white p-2 rounded">Start</button>
        <button onClick={captureAndRegister} className="bg-purple-500 text-white p-2 rounded">Capture</button>
      </div>
    </div>
  );
};

export default RegisterFace;
