import React from "react";
import RegisterFace from "./components/RegisterFace";
import LiveCam from "./components/LiveCam";
import ChatWidget from "./components/ChatWidget";

export default function App() {
  const [tab, setTab] = React.useState("register");
  const BTN = (id, label) => (
    <button
      onClick={() => setTab(id)}
      className={`px-4 py-2 rounded-t ${
        tab === id ? "bg-purple-600 text-white" : "bg-gray-200"
      }`}
    >
      {label}
    </button>
  );

  return (
    <div className="max-w-5xl mx-auto mt-6">
      <div className="flex">{BTN("register", "Register")} {BTN("live", "Live")} {BTN("chat", "Chat")}</div>
      <div className="border p-4 rounded-b bg-white">
        {tab === "register" && <RegisterFace />}
        {tab === "live" && <LiveCam />}
        {tab === "chat" && <ChatWidget />}
      </div>
    </div>
  );
}
