import React, { useRef, useEffect } from "react";

const LiveCam = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const intervalRef = useRef(null);

  const startCamera = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    videoRef.current.srcObject = stream;

    videoRef.current.onloadedmetadata = () => {
      videoRef.current.play();
      startRecognitionLoop();
    };
  };

  const startRecognitionLoop = () => {
    intervalRef.current = setInterval(async () => {
      const video = videoRef.current;
      const canvas = canvasRef.current;
      if (!video || !canvas) return;

      const ctx = canvas.getContext("2d");
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
      const imageDataURL = canvas.toDataURL("image/jpeg");

      try {
        const res = await fetch("http://localhost:5000/recognize", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ image: imageDataURL }),
        });

        const result = await res.json(); // expected: [{ name, x, y, width, height }, ...]

        // Clear previous drawings
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

        result.forEach((face) => {
          ctx.strokeStyle = "#00FF00";
          ctx.lineWidth = 2;
          ctx.strokeRect(face.x, face.y, face.width, face.height);
          ctx.fillStyle = "rgba(0,0,0,0.5)";
          ctx.fillRect(face.x, face.y - 20, face.name.length * 10, 20);
          ctx.fillStyle = "#00FF00";
          ctx.fillText(face.name, face.x + 5, face.y - 5);
        });
      } catch (err) {
        console.error("Recognition error:", err);
      }
    }, 1000); // Run every second
  };

  useEffect(() => {
    return () => clearInterval(intervalRef.current); // Cleanup on unmount
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-xl mb-2">Live Recognition</h2>
      <div className="relative w-full max-w-[640px]">
        <video ref={videoRef} className="rounded border w-full" autoPlay muted />
        <canvas
          ref={canvasRef}
          className="absolute top-0 left-0 w-full h-full"
        />
      </div>
      <button
        onClick={startCamera}
        className="mt-4 bg-blue-500 text-white p-2 rounded"
      >
        Start Camera
      </button>
    </div>
  );
};

export default LiveCam;
